            uint256 newPoolQ_price
            // uint256 newPoolQ_time
        ) external onlyOwner {
            poolRewardRate = newPoolRewardRate * 100;
            poolQ_price = newPoolQ_price* (10 ** decimals);
            // poolQ_time = newPoolQ_time;
        }


